INSERT INTO items VALUES(31,9681,58,'OTHER','ORD 23-48','','X. ORD 23-48 An Ordinance Amending Chapter 7 ("Buildings"), Article 2 ("Existing Building Code") of the Oak Park Village Code to Adopt the 2021 International Existing Building Code as Recommended by the Building Code Advisory Commission','33e8e6ab-d2d0-4ebf-bd1e-5ce0ac4ab523',2,'2024-10-28T01:59:44','2024-11-05T04:39:00',replace('X. ORD 23-48 An Ordinance Amending Chapter 7 (“Buildings”), Article 2 (“Existing\nBuilding Code”) of the Oak Park Village Code to Adopt the 2021\nInternational Existing Building Code as Recommended by the Building Code\nAdvisory Commission\nIt was moved by Trustee Robinson, seconded by Trustee Parakkat, that this\nOrdinance be adopted. The motion was approved. The roll call on the vote was\nas follows:\nAYES: 7 - Village President Scaman, Village Trustee Buchanan, Village Trustee Enyia, Village\nTrustee Parakkat, Village Trustee Robinson, Village Trustee Straw, and Village\nTrustee Wesley\nNAYS: 0\nABSENT: 0\n','\n',char(10)),NULL);
