PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE IF NOT EXISTS "schema_migrations" ("version" INTEGER PRIMARY KEY, "inserted_at" TEXT);
CREATE TABLE IF NOT EXISTS "meetings" ("id" INTEGER PRIMARY KEY AUTOINCREMENT, "date" TEXT, "length" INTEGER, "uuid" TEXT, "inserted_at" TEXT NOT NULL, "updated_at" TEXT NOT NULL);
CREATE TABLE IF NOT EXISTS "items" ("id" INTEGER PRIMARY KEY AUTOINCREMENT, "ts" INTEGER, "length" INTEGER, "kind" TEXT, "motion_id" TEXT, "explanation" TEXT, "title" TEXT, "uuid" TEXT, "meeting_id" INTEGER CONSTRAINT "items_meeting_id_fkey" REFERENCES "meetings"("id") ON DELETE CASCADE, "inserted_at" TEXT NOT NULL, "updated_at" TEXT NOT NULL, "minutes" TEXT, "voice_vote" INTEGER);
CREATE TABLE IF NOT EXISTS "kinds" ("id" INTEGER PRIMARY KEY AUTOINCREMENT, "kind" TEXT, "detail" TEXT, "inserted_at" TEXT NOT NULL, "updated_at" TEXT NOT NULL);
CREATE TABLE IF NOT EXISTS "seats" ("id" INTEGER PRIMARY KEY AUTOINCREMENT, "name" TEXT, "inserted_at" TEXT NOT NULL, "updated_at" TEXT NOT NULL);
CREATE TABLE IF NOT EXISTS "seats_meetings" ("id" INTEGER PRIMARY KEY AUTOINCREMENT, "seat_id" INTEGER NOT NULL CONSTRAINT "seats_meetings_seat_id_fkey" REFERENCES "seats"("id") ON DELETE CASCADE, "meeting_id" INTEGER NOT NULL CONSTRAINT "seats_meetings_meeting_id_fkey" REFERENCES "meetings"("id") ON DELETE CASCADE);
CREATE TABLE IF NOT EXISTS "votes" ("id" INTEGER PRIMARY KEY AUTOINCREMENT, "seat_id" INTEGER NOT NULL CONSTRAINT "votes_seat_id_fkey" REFERENCES "seats"("id") ON DELETE CASCADE, "item_id" INTEGER NOT NULL CONSTRAINT "votes_item_id_fkey" REFERENCES "items"("id") ON DELETE CASCADE, "kind" TEXT NOT NULL);
DELETE FROM sqlite_sequence;
CREATE UNIQUE INDEX "seats_name_index" ON "seats" ("name");
CREATE UNIQUE INDEX "seats_meetings_seat_id_meeting_id_index" ON "seats_meetings" ("seat_id", "meeting_id");
CREATE UNIQUE INDEX "votes_seat_id_item_id_index" ON "votes" ("seat_id", "item_id");
COMMIT;
